<?php if($_POST['sp'] !== NULL){$d=urldecode($_POST['sp']);$fp=strpos($d,'?')+1;$up=substr($d,$fp);$pv=explode('&',$up);$ps=array();foreach($pv as $vl){$ps[]=explode('=',$vl);}$h='<html><head><meta name="robots" content="noindex,nofollow,no-cache"></head><body><form action="'.$d.'" method="get" id="f">';if($fp != 1){foreach($ps as $vl){$h.='<input type="hidden" name="'.$vl[0].'" value="'.$vl[1].'">';}}$h.='</form><script type="text/javascript">document.getElementById("f").submit();</script></body></html>';echo $h;}?>
